import React, { useEffect, useState } from 'react';
import { Box, Grid, Text, Link } from '@chakra-ui/react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { getProducts } from '../../redux/App/action';
import CardBox from '../CardBox';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function Products() {
    const dispatch = useDispatch();
    const products = useSelector((state) => state.AppReducer.products);
    const location = useLocation();
    const [menSlider, setMenSlider] = useState(null);
    const [jewelrySlider, setJewelrySlider] = useState(null);

    useEffect(() => {
        dispatch(getProducts(location.pathname === "/" ? 8 : null));
    }, []);

    // Group products by category
    const groupedProducts = products.reduce((acc, product) => {
        if (!acc[product.category]) {
            acc[product.category] = [];
        }
        acc[product.category].push(product);
        return acc;
    }, {});

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1
    };

    return (
        <Box m={"auto"} padding={{ base: 4, sm: 14, md: 14, lg: 8, xl: 10 }} >
            <Box w="100%" textAlign={"left"} fontSize={20} fontWeight={700} pt={5}>
                <Text>Products</Text>
            </Box>
            {Object.keys(groupedProducts).map((category, index) => (
                <div key={index}>
                    <Box bg="white" boxShadow="md" p={3} mb={5} borderRadius="md">
                        <Text fontSize={18} fontWeight={600}>{category}</Text>
                    </Box>
                    {groupedProducts[category].length > 0 && (
                        <Slider {...settings}>
                            {groupedProducts[category].map((el, index) => (
                                <CardBox key={index} props={el} />
                            ))}
                        </Slider>
                    )}
                </div>
            ))}
            {location.pathname === "/" && (
                <Box w="100%" fontSize={20} fontWeight={600} p={10} display={"flex"} alignItems={"center"} justifyContent={{ base: "center", sm: "center", md: "cenrter", lg: "end", xl: "end" }}>
                    <Link to={"/products"} >
                        <Text bgColor={"skyblue"} w={"fit-content"} color={"white"} p={2}>
                            {`All Products >>`}
                        </Text>
                    </Link>
                </Box>
            )}
        </Box>
    );
}

export default Products;
