import React, { Component } from "react";
import Slider from "react-slick";

export default class Responsive extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: true,
      speed: 1000,
      className: "center",
      centerMode: true,
      slidesToShow: 3,
      slidesToScroll: 3,
      lazyload:true,
      initialSlide: 4,
      autoplay: true,
      autoplaySpeed: 2000,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 890,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 580,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div style={{width:"95%",margin:"auto",height:"400px",paddingTop:10}}>
        <Slider {...settings}>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/71YXzeOuslL._AC_UY879_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/61sbMiUnoGL._AC_UL640_QL65_ML3_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/71YAIFU48IL._AC_UL640_QL65_ML3_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/81XH0e8fefL._AC_UY879_.jpg" alt="" />
          </div>
          {/* Add more slides here */}
          {/* For example: */}
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/61pHAEJ4NML._AC_UX679_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/51eg55uWmdL._AC_UX679_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/71kWymZ+c+L._AC_SX679_.jpg" alt="" />
          </div>
          <div style={slideItemStyle}>
            <img src="https://fakestoreapi.com/img/81QpkIctqPL._AC_SX679_.jpg" alt="" />
          </div>
        </Slider>
      </div>
    );
  }
}

const slideItemStyle = {
  height: "100%",
  margin: "auto",
  border: "1px solid blue",
  width: "31.66px",
  boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)", // Add box shadow
  transition: "transform 0.3s ease", // Add animation
};

// Add hover animation
slideItemStyle[':hover'] = {
  transform: "scale(1.05)",
};
