import React, { useEffect, useState } from 'react';
import { Box, Flex, IconButton, Menu, MenuButton, MenuItem, MenuList, Image } from '@chakra-ui/react';
import Logo from '../Logo/Logo';
import Badge from "@material-ui/core/Badge";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import { useLocation, useNavigate } from 'react-router-dom';
import { VscAccount } from "react-icons/vsc";
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { GetCart } from '../../redux/App/action';

function Navbar() {
    const [profileImg, setProfileImg] = useState(""); // State to store profile picture URL
    const [total, setTotal] = useState("");
    const navigate = useNavigate();
    const location = useLocation();
    const dispatch = useDispatch();
    const isAuth = useSelector((state) => state.AuthReducer.isAuth);
    const cart = useSelector((state) => state.AppReducer.cart);
    const t1 = localStorage.getItem("total");

    useEffect(() => {
        dispatch(GetCart());
        let t = 0;
        for (let i = 0; i < cart.length; i++) {
            t += cart[i].quantity;
        }
        setTotal(t);
    }, [cart.length]);

    useEffect(() => {
        // Set the profile picture URL from localStorage or use a default URL
        const storedProfileImg = localStorage.getItem("profileImg");
        setProfileImg(storedProfileImg && storedProfileImg !== "undefined" ? storedProfileImg : "https://ibb.co/6W25zQT");
    }, []);

    return (
        <Flex p={10} zIndex={700} w={"100%"} boxShadow="sm" height={"90px"} alignItems={"center"} justifyContent="space-between" bgColor={"white"} position="sticky" top={0}>
            <Link to={"/"}>
                <Logo />
            </Link>

            <Box display={"flex"} width={{ base: "40%", sm: "35%", md: "30%", lg: "25%", xl: "10%" }} alignItems={"center"} justifyContent={"space-between"}>
                <Link to={"/cart"}>
                    <Badge overlap="rectangular" color="secondary" badgeContent={t1}>
                        <ShoppingCartIcon color="primary" style={{ fontSize: "30px" }} />
                    </Badge>
                </Link>
                <Menu>
                    <MenuButton
                         
                    />

                    <MenuList bgColor={"black"} color={"skyblue"} fontWeight={700} fontSize={20}>
                        {isAuth ? <MenuItem onClick={() => {
                            localStorage.removeItem("isAuth");
                            navigate("/login");
                        }}>Logout</MenuItem> :
                            <Link to={"/login"}>
                                <MenuItem>
                                    Login
                                </MenuItem>
                            </Link>}

                        <Link to={"/profile"}>
                            <MenuItem>
                                Profile
                            </MenuItem>
                        </Link>
                        <Link to={"/Products"}>
                            <MenuItem>
                                My Store
                            </MenuItem>
                        </Link>

                    </MenuList>
                </Menu>
            </Box>
        </Flex>
    );
}

export default Navbar;
