import React, { useEffect } from 'react';
import { Box, Grid, Text, Link, useLocation } from '@chakra-ui/react';
import { useSelector, useDispatch } from 'react-redux';
import { getProducts } from '../../redux/App/action';
import CardBox from '../CardBox';

function Products() {
    const dispatch = useDispatch();
    const products = useSelector((state) => state.AppReducer.products);
    const location = useLocation();

    useEffect(() => {
        dispatch(getProducts(location.pathname === "/" ? 8 : null));
    }, []);

    // Group products by category
    const groupedProducts = products.reduce((acc, product) => {
        if (!acc[product.category]) {
            acc[product.category] = [];
        }
        acc[product.category].push(product);
        return acc;
    }, {});

    return (
        <Box m={"auto"} padding={{ base: 4, sm: 14, md: 14, lg: 8, xl: 10 }} >
            <Box w="100%" textAlign={"left"} fontSize={20} fontWeight={700} pt={5}>
                <Text>Products</Text>
            </Box>
            {Object.keys(groupedProducts).map((category, index) => (
                <div key={index}>
                    <Text fontSize={18} fontWeight={600} pt={3}>{category}</Text>
                    {groupedProducts[category].length > 0 && (
                        <Grid
                            m={"auto"}
                            templateRows='repeat(2, 1fr)'
                            templateColumns={{ base: 'repeat(1, 1fr)', sm: 'repeat(1, 1fr)', md: 'repeat(2, 1fr)', lg: 'repeat(3, 1fr)', xl: 'repeat(4, 1fr)' }}
                            w={{ base: '80%', sm: '80%', md: '80%', lg: '100%', xl: '100%' }}
                            gap={5}
                            h={"40%"}
                        >
                            {groupedProducts[category].map((el, index) => (
                                <CardBox key={index} props={el} />
                            ))}
                        </Grid>
                    )}
                </div>
            ))}
            {location.pathname === "/" && (
                <Box w="100%" fontSize={20} fontWeight={600} p={10} display={"flex"} alignItems={"center"} justifyContent={{ base: "center", sm: "center", md: "cenrter", lg: "end", xl: "end" }}>
                    <Link to={"/products"} >
                        <Text bgColor={"skyblue"} w={"fit-content"} color={"white"} p={2}>
                            {`All Products >>`}
                        </Text>
                    </Link>
                </Box>
            )}
        </Box>
    );
}

export default Products;
