import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

// Redux Actions
const getProducts = (limit) => {
  return {
    type: 'GET_PRODUCTS',
    payload: { limit }
  };
};

// Redux Reducer
const initialState = {
  products: []
};

const appReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_PRODUCTS':
      // Logic to fetch products from API or any other source
      const mockProducts = [
        { id: 1, name: "Product 1", category: "Men's Clothing", price: "$20" },
        { id: 2, name: "Product 2", category: "Men's Clothing", price: "$25" },
        { id: 3, name: "Product 3", category: "Jewelry", price: "$30" },
        { id: 4, name: "Product 4", category: "Jewelry", price: "$15" },
        { id: 5, name: "Product 5", category: "Electronics", price: "$100" },
        { id: 6, name: "Product 6", category: "Electronics", price: "$50" }
      ];
      const products = action.payload.limit ? mockProducts.slice(0, action.payload.limit) : mockProducts;
      return { ...state, products };
    default:
      return state;
  }
};

// Main Component
function App() {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Products} />
        <Route path="/mens-clothing" render={(props) => <CategoryProducts {...props} category="Men's Clothing" />} />
        <Route path="/jewelry" render={(props) => <CategoryProducts {...props} category="Jewelry" />} />
        {/* Add more routes for other categories if needed */}
      </Switch>
    </Router>
  );
}

// Products Component
function Products() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);
  const location = useLocation();

  useEffect(() => {
    dispatch(getProducts(location.pathname === "/" ? 8 : null));
  }, []);

  return (
    <div>
      <h1>Products</h1>
      <div>
        {products.map((product, index) => (
          <CardBox key={index} props={product} />
        ))}
      </div>
      {location.pathname === "/" && (
        <Link to="/products">View all products</Link>
      )}
    </div>
  );
}

// CategoryProducts Component
function CategoryProducts({ category }) {
  const products = useSelector((state) => state.products);

  const filteredProducts = products.filter((product) => product.category === category);

  return (
    <div>
      <h1>{category}</h1>
      <div>
        {filteredProducts.map((product, index) => (
          <CardBox key={index} props={product} />
        ))}
      </div>
      <Link to="/">Go back to all products</Link>
    </div>
  );
}

// CardBox Component
function CardBox({ props }) {
  return (
    <div>
      <h3>{props.name}</h3>
      <p>Category: {props.category}</p>
      <p>Price: {props.price}</p>
    </div>
  );
}

export default App;
